// InfoBox.js
import { animate, motion, useMotionValue, useTransform } from "framer-motion";

import Confetti from "react-confetti";
import React from "react";
import html2canvas from "html2canvas";
import { useWindowSize } from "react-use";

const InfoBox = () => {
  const count1 = useMotionValue(0);
  const rounded1 = useTransform(count1, (latest) => Math.round(latest));

  const count2 = useMotionValue(0);
  const rounded2 = useTransform(count2, (latest) => Math.round(latest));

  const count3 = useMotionValue(0);
  const rounded3 = useTransform(count3, (latest) => Math.round(latest));

  const count4 = useMotionValue(0);
  const rounded4 = useTransform(count4, (latest) => Math.round(latest));

  React.useEffect(() => {
    const controls = animate(count1, 49, {
      delay: 0.5,
      duration: 2,
    });

    return controls.stop;
  }, []);

  React.useEffect(() => {
    const controls = animate(count2, 27, {
      delay: 2.5,
      duration: 2,
    });

    return controls.stop;
  }, []);

  React.useEffect(() => {
    const controls = animate(count3, 60, {
      delay: 4.5,
      duration: 2,
    });

    return controls.stop;
  }, []);

  React.useEffect(() => {
    const controls = animate(count4, 49, {
      delay: 5.5,
      duration: 2,
    });

    return controls.stop;
  }, []);

  const cardRef = React.useRef<HTMLDivElement>(null);

  const handleCapture = () => {
    if (!cardRef.current) return;
    html2canvas(cardRef.current, {}).then((canvas) => {
      const link = document.createElement("a");
      link.download = "card.png";
      link.href = canvas.toDataURL("image/png");
      link.click();
    });
  };

  const { width, height } = useWindowSize();

  return (
    <>
      <Confetti width={width} height={height} />
      <main
        className="w-screen h-screen flex flex-col"
        style={{
          backgroundImage:
            "url('data:image/jpeg;base64,')",
          backgroundSize: "cover",
          backgroundPosition: "center",
        }}
      >
        <section className="w-full flex-grow  flex items-center justify-center p-10">
          <div className="flex flex-col w-full max-w-4xl text-zinc-50">
            <div className="bg-gray-900 py-16 pb-10 rounded-3xl" ref={cardRef}>
              <div className="mx-auto max-w-7xl px-6 lg:px-8">
                <div className="mx-auto max-w-2xl lg:max-w-none">
                  <div className="text-center space-y-4">
                    <h2 className="text-3xl font-bold tracking-tight text-white sm:text-4xl leading-loose">
                      Juntos, celebramos <br />
                      um 2023 extraordinário!
                    </h2>
                    <p className="text-md leading-8 text-gray-300">
                      A sua jornada na Academia Digital Einstein foi repleta de
                      descobertas e conquistas.
                      <br /> Vamos relembrar juntos?
                    </p>
                  </div>
                  <dl className="mt-16 grid grid-cols-1 gap-0.5 overflow-hidden rounded-2xl text-center sm:grid-cols-2 lg:grid-cols-4">
                    <div className="flex flex-col bg-white/5 p-8 px-">
                      <dt className="text-xs font-semibold leading-6 text-gray-300">
                        dias que você se tornou membro da Academia Digital
                        Einstein.
                      </dt>
                      <dd className="order-first text-5xl mb-4 font-semibold tracking-tight text-white">
                        <motion.span
                          initial={{ opacity: 0, y: -50 }}
                          animate={{
                            opacity: 1,
                            y: 0,
                            transition: {
                              delay: 0.5,
                              duration: 0.5,
                            },
                          }}
                        >
                          {rounded1}
                        </motion.span>
                      </dd>
                    </div>
                    <div className="flex flex-col bg-white/5 p-8 px-">
                      <dt className="text-xs font-semibold leading-6 text-gray-300">
                        cada minuto, uma nova descoberta!
                      </dt>
                      <dd className="order-first text-5xl mb-4 font-semibold tracking-tight text-white">
                        <motion.span
                          initial={{ opacity: 0, y: -50 }}
                          animate={{
                            opacity: 1,
                            y: 0,
                            transition: {
                              delay: 2.5,
                              duration: 0.5,
                            },
                          }}
                        >
                          {rounded2}
                        </motion.span>
                      </dd>
                    </div>
                    <div className="flex flex-col bg-white/5 p-8 px-">
                      <dt className="text-xs font-semibold leading-6 text-gray-300">
                        você está acima da média geral de membros.
                      </dt>
                      <dd className="order-first text-5xl mb-4 font-semibold tracking-tight text-white">
                        <motion.span
                          initial={{ opacity: 0, y: -50 }}
                          animate={{
                            opacity: 1,
                            y: 0,
                            transition: {
                              delay: 5,
                              duration: 0.5,
                            },
                          }}
                        >
                          {rounded3}
                        </motion.span>
                        <motion.span
                          initial={{ opacity: 0, y: -50 }}
                          animate={{
                            opacity: 1,
                            y: 0,
                            transition: {
                              delay: 5,
                              duration: 0.5,
                            },
                          }}
                        >
                          %
                        </motion.span>
                      </dd>
                    </div>
                    <div className="flex flex-col bg-white/5 p-8 px-4">
                      <dt className="text-xs font-semibold leading-6 text-gray-300">
                        Cursos expandindo horizontes, passo a passo.
                      </dt>
                      <dd className="order-first text-5xl mb-4 font-semibold tracking-tight text-white">
                        <motion.span
                          initial={{ opacity: 0, y: -50 }}
                          animate={{
                            opacity: 1,
                            y: 0,
                            transition: {
                              delay: 7.5,
                              duration: 0.5,
                            },
                          }}
                        >
                          {rounded4}
                        </motion.span>
                      </dd>
                    </div>
                  </dl>

                  <div className=" flex p-8  justify-center align-middle ">
                    <img src="data:image/png;base64," />
                  </div>
                </div>
              </div>
            </div>

            <div className="flex align-middle justify-center mt-10">
              <button
                onClick={() => handleCapture()}
                className="relative rounded px-5 py-2.5 overflow-hidden group bg-slate-800 relative hover:bg-gradient-to-r hover:from-slate-500 hover:to-slate-400 text-white hover:ring-2 hover:ring-offset-2 hover:ring-slate-800 transition-all ease-out duration-300"
              >
                <span className="absolute right-0 w-8 h-32 -mt-12 transition-all duration-1000 transform translate-x-12 bg-slate opacity-10 rotate-12 group-hover:-translate-x-40 ease"></span>
                <span className="relative text-xl">
                  Salvar card para compartilhar 🎉
                </span>
                <span className="hidden">{width}</span>
              </button>
            </div>
          </div>
        </section>
      </main>
    </>
  );
};
